import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { Dropdown, Menu} from 'semantic-ui-react'
import "./Account.css"

class Account extends Component {
  state = {
    ids:["Account","Resume","Password","LogOut"],
    cards:["_Account","_Resume","_Password","_LogOut"],
    Name : "Tim",
    Email : "boyuli3@illinois.edu"
  }

  onClick = (id) =>{
    var elem1 = document.getElementById(this.state.ids[0]);
    var elem2 = document.getElementById(this.state.ids[1]);
    var elem3 = document.getElementById(this.state.ids[2]);
    var elem4 = document.getElementById(this.state.ids[3]);
    elem1.classList.remove("active");
    elem2.classList.remove("active");
    elem3.classList.remove("active");
    elem4.classList.remove("active");
    var elem5 = document.getElementById(id);
    elem5.classList.add("active");

    var card1 = document.getElementById(this.state.cards[0]);
    var card2 = document.getElementById(this.state.cards[1]);
    var card3 = document.getElementById(this.state.cards[2]);
    var card4 = document.getElementById(this.state.cards[3]);
    card1.style.display = "none";
    card2.style.display = "none";
    card3.style.display = "none";
    card4.style.display = "none";
    var card_id = "_" + id;
    var card5 = document.getElementById(card_id);
    card5.style.display = "block";

  }
  render() {

	    return (
        <div className="ui grid">
          <div className="four wide column">
            <div className="ui vertical fluid tabular menu">
              <a className="item" id = "Account" onClick = {this.onClick.bind(this,"Account")}>
                Account
              </a>
              <a className="item" id = "Resume" onClick = {this.onClick.bind(this,"Resume")}>
                Resume
              </a>
              <a className="item" id = "Password" onClick = {this.onClick.bind(this,"Password")}>
                Password
              </a>
              <a className="item" id = "LogOut" onClick = {this.onClick.bind(this,"LogOut")}>
                LogOut
              </a>
          </div>
        </div>

        <div className="twelve wide stretched column">
          <div className="ui segment _Account" id="_Account">
            <div className = "account_row">
              <h4>Name: {this.state.Name}</h4>
            </div>
            <div className = "account_row">
              <h4>Email: {this.state.Email}</h4>
            </div>
            <div className = "account_row">
              <h4>Company:</h4><input placeholder="input..." className="account_input"></input>
            </div>
            <div className = "account_row">
              <h4>Industry:</h4><input placeholder="input..." className="account_input"></input>
            </div>
            <div className = "account_row">
              <h4>Location:</h4><input placeholder="input..." className="account_input"></input>
            </div>
            <div className = "account_row">
              <h4>Type:</h4><input placeholder="input..." className="account_input"></input>
            </div>
            <div className = "account_row">
              <h4>Size:</h4><input placeholder="input..." className="account_input"></input>
            </div>
          </div>

          <div className="ui segment _Resume" id="_Resume">
            Resume
          </div>

          <div className="ui segment _Password" id="_Password">
            Password
          </div>

          <div className="ui segment _LogOut" id="_LogOut">
            LogOut
          </div>
        </div>
      </div>

	    )
  }

}

export default Account
