import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { Dropdown, Menu} from 'semantic-ui-react'
import './Account.css'
class Account extends Component {
  state = {
    ids: ["Account","Resume","Password","LogOut"]
  }

  onClick = (id) => {
    var i = 0
    var elem1 = document.getElementById(this.state.ids[0]);
    var elem2 = document.getElementById(this.state.ids[1]);
    var elem3 = document.getElementById(this.state.ids[2]);
    var elem4 = document.getElementById(this.state.ids[3]);
    elem1.classList.remove("active");
    elem2.classList.remove("active")
    elem3.classList.remove("active")
    elem4.classList.remove("active")

    var elem5 = document.getElementById(id);
    elem5.classList.add("active")
  }

  render() {

	    return (
          <div className = "AccountContent">
	           <div className="ui grid">
               <div className="six wide column">
                 <div className="ui vertical fluid tabular menu">
                   <a className="item" id = "Account" onClick = {this.onClick.bind(this,"Account")}>Account</a>
                   <a className="item" id = "Resume" onClick = {this.onClick.bind(this,"Resume")}>Starred Resume</a>
                   <a className="item" id = "Password" onClick = {this.onClick.bind(this,"Password")}>Change Password</a>
                   <a className="item" id = "LogOut" onClick = {this.onClick.bind(this,"LogOut")}>Log Out</a>

                 </div>
               </div>


               <div className="ten wide stretched column">
                 <div className="ui segment Account">
                   This is an stretched grid column. This segment will always match the tab height
                 </div>
               </div>
             </div>
          </div>
	    )
  }

}

export default Account
