import React, { Component } from 'react'
import NewWindow from 'react-new-window'
import {MovieImg, ModalImgSection, ModalTextSection} from './PostedJobs.module.scss'
import ReactModal from 'react-modal';


class ResumeModal extends Component {
  constructor() {
    super();
    this.baseImgUrl = "https://image.tmdb.org/t/p/w1280/";
  }

  componentDidMount() {
    document.body.addEventListener('keydown', this.handleKeyDown);
  }

  // componentWillUnMount() {
  //   document.body.removeEventListener('keydown', this.handleKeyDown);
  // }


  render() {
    const {isShow, closeModal, findNext, findPrev, src } = this.props;
    if (!src) {
      return null;
    }

    console.log(src);
    return (
        <ReactModal isOpen={isShow}>
            <button className="modal-close" onClick={closeModal} onKeyDown={this.handleKeyDown}>&times;</button>
            <div className={ModalImgSection}>
                <button className="modal-prev" onClick={findPrev} onKeyDown={this.handleKeyDown}>&#10094;</button>
                <img className={MovieImg} src={"https://image.tmdb.org/t/p/w1280/" + src.poster_path}/>
                <button className="modal-next" onClick={findNext} onKeyDown={this.handleKeyDown}>&#10095;</button>
            </div>

            <div className={ModalTextSection}>
                <h3>{src.title}</h3>
                <p>Release Date: {src.release_date}</p>
                <p>Rating: {src.vote_average}</p>
                <p>Language: {src.original_language}</p>
                <p>Popularity: {src.popularity}</p>
                <p>Overview: {src.overview}.</p>
            </div>
        </ReactModal>
    );

  }}

export default ResumeModal
